/* Se utiliza el patrn Strategy para crear los algoritmos de busqueda en profundidad, a futuro si
se requiere buscar una secuencia de ADN particular (no mutante), se pueden aadir nuevos algoritmos
de busqueda*/

export class SearchContext {
  private strategy: ISearchStrategy;
  private size: number;

  constructor(size: number) {
    this.size = size;
  }

  public setStrategy(strategy: ISearchStrategy) {
    this.strategy = strategy;
  }

  public search(i: number, j: number, dnaMatrix: Nucleotide[][]): boolean {
    const result = this.strategy.search(i, j, this.size, dnaMatrix);
    return result;
  }
}

const searchContext = new SearchContext(0);
export default searchContext;

import { Nucleotide } from "../../../models/nucleotide.model";
import { ISearchStrategy } from "../i-strategy";

export class SearchRightStrategy implements ISearchStrategy {
  private matchCounter: number = 1;
  private dnaMatrix: Nucleotide[][] = [];

  search(
    i: number,
    j: number,
    size: number,
    dnaMatrix: Nucleotide[][]
  ): boolean {
    if (i <= size && j <= size - 4 && !dnaMatrix[i][j].getRight()) {
      this.dnaMatrix = dnaMatrix;
      return this.check(i, j);
    }
    return false;
  }

  check(i: number, j: number): boolean {
    if (
      this.dnaMatrix[i][j].getValue() === this.dnaMatrix[i][j + 1].getValue()
    ) {
      this.matchCounter++;
      this.dnaMatrix[i][j + 1].setRight(true);

      return this.matchCounter === 4 ? true : this.check(i, j + 1);
    }
    this.dnaMatrix[i][j].setRight(false);
    this.matchCounter = 1;
    return false;
  }
}

const searchRightStrategy = new SearchRightStrategy();
export default searchRightStrategy;

import { Nucleotide } from "../../../models/nucleotide.model";
import { ISearchStrategy } from "../i-strategy";

export class SearchRightDownStrategy implements ISearchStrategy {
  private matchCounter: number = 1;
  private dnaMatrix: Nucleotide[][] = [];

  search(
    i: number,
    j: number,
    size: number,
    dnaMatrix: Nucleotide[][]
  ): boolean {
    if (i <= size - 4 && j <= size - 4 && !dnaMatrix[i][j].getRightDown()) {
      this.dnaMatrix = dnaMatrix;
      return this.check(i, j);
    }
    return false;
  }

  check(i: number, j: number): boolean {
    if (
      this.dnaMatrix[i][j].getValue() ===
      this.dnaMatrix[i + 1][j + 1].getValue()
    ) {
      this.matchCounter++;
      this.dnaMatrix[i][j + 1].setRightDown(true);

      return this.matchCounter === 4 ? true : this.check(i + 1, j + 1);
    }
    this.dnaMatrix[i][j].setRightDown(false);
    this.matchCounter = 1;
    return false;
  }
}

const searchRightDown = new SearchRightDownStrategy();
export default searchRightDown;

