      // @ts-ignore
      const [sequences, count] = await queryRunner.manager.findAndCount(DNASequencesEntity, {
        subjectType,
      });
      return count;
    } catch (error) {
      console.log("[ERROR] couting DNA sequences by subject type %o", error);
      throw new Error(error);
    }
